import React, { Component } from 'react';
import './App.css';
import { Route, Switch, Link } from 'react-router-dom';
import axios from 'axios'

class App extends Component {
  state = {
    name: ''
  }

  getName = name => {
    this.setState({
      name
    })
  }

  saveName = () => {
    this.setState({
      name: this.state.name
    }, () => {
      localStorage.setItem('name', JSON.stringify(this.state.name))
    })
  }

  componentDidMount() {
    const name = JSON.parse(localStorage.getItem('name'))
    if (name) {
      this.setState({
        name
      })
    }
  }
  render() {
    return (
      <div className="App">
        <Link to='/'>Home</Link>
        <Link to='/shops'>Shops</Link>
        <Switch>
          <Route path='/' exact render={() => <Home name={this.state.name} updateName={this.getName} saveName={this.saveName} />} />
          <Route path='/shops' render={() => <Shops hats={this.props.Hats} shoes={this.props.Shoes} name={this.state.name} />} />
        </Switch>
      </div>
    );
  }
}

const Home = (props) => {
  return (
    <div>
      <h1>Home</h1>
      <form>
        <p>Enter your name:</p>
        <div>
          <input type='text' onChange={e => props.updateName(e.target.value)} name='name' />
          <button onClick={props.saveName} >Submit</button>
        </div>
      </form>
    </div>
  )
}

class Shops extends Component {
  state = {
    hats: this.props.hats,
    shoes: this.props.shoes,
    cartList: []
  }



  componentDidMount(){
    axios
    .get('http://localhost:8080/shop')
    .then(({data}) => {
        console.log(data.shoppingCart.cartList[0].title)
        console.log(data.shoppingCart.cartList[0].title)
    }
    )
  }
  // componentDidUpdate(){
  //  axios
  //  .post('http://localhost:8080/pushcart', {
  //    cartList: this.state.cartList
  //  })
  //  .then(res => console.log(res))

  // }
  updatehatsCart = (id) => {
    let cartItem = this.props.hats.find((item, i) => {
      return item.id === id
  })
    this.setState({
      cartList: this.state.cartList.concat(
        {
          title: cartItem.title,
          description:cartItem.description,
          price: cartItem.price,
          image:cartItem.image
        })
    }, () => {
       axios
        .post('http://localhost:8080/shop', {
          cartList: this.state.cartList
        })
        .then(res => console.log(res))

    })
  }
  updateshoesCart = (id) => {
    let cartItem = this.props.shoes.find((item, i) => {
      return item.id === id
  })
    this.setState({
      cartList: this.state.cartList.concat(
        {
          title: cartItem.title,
          description:cartItem.description,
          price: cartItem.price,
          image:cartItem.image
        })
    })
  }
  render() {
    const cartList = this.state.cartList.map((cart, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={cart.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{cart.title}</h5>
              <p className="card-text">{cart.description}</p>
              <p>{cart.price}</p>
              <button className="btn btn-primary" type='button'>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <div>
          <p style={{ "float": "right" }}>Welcome {this.props.name}</p>
        </div>
        <h1>Shops</h1>

        <Link to='/shops/hats'>Hats</Link>
        <Link to='/shops/shoes'>Shoes</Link>
        <Switch>
          <Route path='/shops/hats' render={() => <Hats hats={this.props.hats} updatehatsCart={this.updatehatsCart} />} />
          <Route path='/shops/shoes' render={() => <Shoes shoes={this.props.shoes} updateshoesCart={this.updateshoesCart}/>} />
        </Switch>
        <Cart cartList={cartList} />
      </div>
    )
  }
}

const Cart = (props) => {
  return (
    <div>
      <h1>Cart List</h1>
      <div className='container'>
        <div className='row'>
          {props.cartList}
        </div>
      </div>
    </div>
  )

}
class Hats extends Component {

  render() {
    const shopList = this.props.hats.map((hat, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={hat.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{hat.title}</h5>
              <p className="card-text">{hat.description}</p>
              <p>{hat.price}</p>
              <button className="btn btn-primary" type='button' onClick={() => this.props.updatehatsCart(hat.id)}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h1>Hats Collection</h1>
        <div className='container' style={{ "margin": "20px" }}>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}

class Shoes extends Component {

  render() {
    const shopList = this.props.shoes.map((shoe, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={shoe.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{shoe.title}</h5>
              <p className="card-text">{shoe.description}</p>
              <p>{shoe.price}</p>
              <button className="btn btn-primary" onClick={() => this.props.updateshoesCart(shoe.id)}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h1>Shoes Collection</h1>
        <div className='container'>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
