import React, { Component } from 'react';
import './App.css';
import { Route, Switch, Link } from 'react-router-dom'

class App extends Component {
  render() {
    return (
      <div className="App">
        <Link to='/'>Home</Link>
        <Link to='/shops'>Shops</Link>
        <Switch>
          <Route path='/' exact component={Home} />
          <Route path='/shops' render={() => <Shops hats={this.props.Hats}/>} />
        </Switch>
      </div>
    );
  }
}

const Home = () => <h1>Home</h1>
// const Shops = () => <h1>H</h1>

const Shops = (props) => {
  return (
    <div>
      <h1>Shops</h1>
      <Link to='/shops/hats'>Hats</Link>
      <Link to='/shops/shoes'>Shoes</Link>
      <Switch>
        <Route path='/shops/hats' render={() => <Shop subject='Hats' hats={props.hats}/>} />
        <Route path='/shops/shoes' render={() => <Shop subject='Shoes' shoes={props.shoes}/>} />
      </Switch>
    </div>
  )
}
class Shop extends Component {

  render(props) {
    const shopList = props.map((todo, i) => (
      <li className="list-group-item">
      <input type="checkbox"  onChange= {()=>this.clickCheckbox(todo.id)} value={todo.complete}/>
      <label className={todo.complete ? 'done': ''} >{todo.name}</label>
      </li>
    ))
  return (
        <div>
          <h1>{props.subject} Collection</h1>
          <div className='row'>
          <div className='col'>
          <div className="card" style={{"width": "18rem"}}>
            <img className="card-img-top" src="..." alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{props.hats}</h5>
                <p className="card-text">{props.description}</p>
                <a href="" className="btn btn-primary">Add to Cart</a>
              </div>
              </div>
              </div>
              </div>
          </div>

      )
  }
}

  export default App;
