import React, { Component } from 'react';
import './App.css';
import { Route, Switch, Link } from 'react-router-dom';
import axios from 'axios';
import Home from './component/Home';
import Shops from './component/Home'


class App extends Component {
  state = {
    name: '',
    temporaryName: ''
  }

  getName = temporaryName => {
    this.setState({
      temporaryName
    })
  }

  saveName = () => {
    this.setState({
      name: this.state.temporaryName
    }, () => {
      localStorage.setItem('name', JSON.stringify(this.state.name))
    })
  }

  componentDidMount() {
    const name = JSON.parse(localStorage.getItem('name'))
    if (name) {
      this.setState({
        name
      })
    }
  }
  render() {
    return (
      <div className="App">
        <nav>
          <ul>
            <li><Link to='/'>Home</Link></li>
            <li><Link to='/shops'>Shops</Link></li>
          </ul>
        </nav>
        <Switch>
          <Route path='/' exact render={() => <Home name={this.state.name} updateName={this.getName} saveName={this.saveName} />} />
          <Route path='/shops' render={() => <Shops hats={this.props.Hats} shoes={this.props.Shoes} name={this.state.name} />} />
        </Switch>
      </div>
    );
  }
}





const Cart = (props) => {
  return (
    <div>
      <h3>Cart List</h3>
      <div className='container'>
        <div className='row'>
          {props.cartList}
        </div>
      </div>
    </div>
  )

}
class Hats extends Component {

  render() {
    const shopList = this.props.hats.map((hat, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={hat.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{hat.title}</h5>
              <p className="card-text">{hat.description}</p>
              <p>{hat.price}</p>
              <button className="btn btn-primary" type='button' onClick={() => this.props.updatehatsCart(hat.id)}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h3>Hats Collection</h3>
        <div className='container' style={{ "margin": "20px" }}>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}

class Shoes extends Component {

  render() {
    const shopList = this.props.shoes.map((shoe, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={shoe.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{shoe.title}</h5>
              <p className="card-text">{shoe.description}</p>
              <p>{shoe.price}</p>
              <button className="btn btn-primary" onClick={() => this.props.updateshoesCart(shoe.id)}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h3>Shoes Collection</h3>
        <div className='container'>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
