import React, { Component } from 'react';
import './App.css';
import { Route, Switch, Link } from 'react-router-dom'

class App extends Component {
  state={
    name:''
  }

  getName = name =>{
    this.setState({
        name
    })
  }

  saveName = ()=>{
    this.setState({
      name:this.state.name
    }, () => {
      localStorage.setItem('name', JSON.stringify(this.state.name))
    })
  }

  componentDidMount(){
    const name = JSON.parse(localStorage.getItem('name'))
    if(name) {
      this.setState({
        name
      })
    }
  }  
  render() {
    return (
      <div className="App">
        <Link to='/'>Home</Link>
        <Link to='/shops'>Shops</Link>
        <Switch>
          <Route path='/' exact render={() => <Home name={this.state.name} updateName={this.getName} saveName={this.saveName}/>}/>
          <Route path='/shops' render={() => <Shops hats={this.props.Hats} shoes={this.props.Shoes} name={this.state.name}/>} />
        </Switch>
      </div>
    );
  }
}

const Home = (props) => {
return(
     <div>
      <h1>Home</h1>
      <form>
        <p>Enter your name:</p>
        <div>
          <input type='text' onChange={e=>props.updateName(e.target.value)} name='name'/>
          <button onClick={props.saveName} >Submit</button>
        </div>
      </form>
     </div>
      )
}

class Shops extends Component {
  state={
    hats:this.props.hats,
    shoes:this.props.shoes,
    cartList: []
  }

  updatehatsCart= () => {
    let cartList = this.props.hats.filter((cart,i)=>{
      return cart.id === 'hat'
    })
    this.setState({
      cartList: cartList
    })
    console.log(this.state.cart)
  }
  render() {
    const cartList = this.state.cartList.map((cart,i) =>(
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={cart.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{cart.title}</h5>
              <p className="card-text">{cart.description}</p>
              <p>{cart.price}</p>
              <button className="btn btn-primary" type='button' onClick={this.props.updatehatsCart}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
  return (
    <div>
      <h1>Shops</h1>
      <h3>Welcome {this.props.name}</h3>
      <Link to='/shops/hats'>Hats</Link>
      <Link to='/shops/shoes'>Shoes</Link>
      <Switch>
        <Route path='/shops/hats' render={() => <Hats hats={this.props.hats} updatehatsCart={this.updatehatsCart}/>} />
        <Route path='/shops/shoes' render={() => <Shoes shoes={this.props.shoes} />} />
      </Switch>
      <h1>Cart List</h1>
      <ul>
        {cartList}
      </ul>
      <Cart cart={this.state.cart} />
    </div>
  )
}
}

const Cart=(props)=>{
  {console.log(props.cart)}
  return(
        <div>
          <h1>Cart List</h1>
          <ul>
            {props.cart}
          </ul>
        </div>
  )

}
class Hats extends Component {


  render() {
    const shopList = this.props.hats.map((hat, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={hat.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{hat.title}</h5>
              <p className="card-text">{hat.description}</p>
              <p>{hat.price}</p>
              <button className="btn btn-primary" type='button' onClick={this.props.updatehatsCart}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h1>Hats Collection</h1>
        <div className='container'>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}
class Shoes extends Component {

  render() {
    const shopList = this.props.shoes.map((shoe, i) => (
      <div>
        <div className='col'>
          <div className="card" style={{ "width": "18rem" }}>
            <img className="card-img-top" src={shoe.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{shoe.title}</h5>
              <p className="card-text">{shoe.description}</p>
              <p>{shoe.price}</p>
              <button className="btn btn-primary">Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    ))
    return (
      <div>
        <h1>Shoes Collection</h1>
        <div className='container'>
          <div className='row'>
            {shopList}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
