import React, { Component } from 'react';
import './App.css';
import { Route, Switch, Link } from 'react-router-dom'

class App extends Component {
  state={
    name:''
  }
  updateName = name =>{
    this.setState({
        name
    })
  }
  storeName = name =>{

  }
  componentDidMount(){
    localStorage.setItem('')

  }  
  render() {
    return (
      <div className="App">
        <Link to='/'>Home</Link>
        <Link to='/shops'>Shops</Link>
        <Switch>
          <Route path='/' exact render={() => <Home name={this.state.name} updateName={this.updateName}/>}/>
          <Route path='/shops' render={() => <Shops hats={this.props.Hats} shoes={this.props.Shoes} />} />
        </Switch>
      </div>
    );
  }
}

const Home = (props) => {
return(
     <div>
      <h1>Home</h1>
      <form>
        <p>Enter your name:</p>
        <div>
          <input type='text' onChange={e=>props.updateName(e.target.value)} name='name'/>
          <button onClick>Submit</button>
        </div>
      </form>
     </div>
      )
}

class Shops extends Component {
  // constructor(props){
  //   super
  // }
  state={
    hats:this.props.hats,
    shoes:this.props.shoes,
    cart: []
  }

  updatehatsCart=()=>{
    let cartList = this.props.hats.filter((cart,i)=>{
      return cart.id === 'hat'
    })
    this.setState({
      cart: cartList
    })
    console.log(this.state.cart)
  }
  render() {
  return (
    <div>
      <h1>Shops</h1>
      <Link to='/shops/hats'>Hats</Link>
      <Link to='/shops/shoes'>Shoes</Link>
      <Switch>
        <Route path='/shops/hats' render={() => <Hats hats={this.props.hats} updatehatsCart={this.updatehatsCart}/>} />
        <Route path='/shops/shoes' render={() => <Shoes shoes={this.props.shoes} />} />
      </Switch>
      <h1>Cart List</h1>
      <ul>
        {this.state.cart}
      </ul>
      {/* <Cart cart={this.state.cart} /> */}
    </div>
  )
}
}

